import React from 'react'
import styled from 'styled-components';
import ArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import ArrowRight from '@mui/icons-material/KeyboardArrowRight';
import { FormatSize } from '@mui/icons-material';

const Container = styled.div`
    width: 100%;
    display: flex;
    position: relative;
    overflow: hidden; 
    `

const Wrapper = styled.div`
    display:flex;
`


const Arrow = styled.div`
    width: 2.8rem;
    height: 2.8rem;
    background-color: rgb(253, 243, 243);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`

const Slide = styled.div`
    display: flex;
    align-items: center;
    background-color: #${props =>props.bg};

`
const ImgContainer = styled.div`
    flex: 1;
    margin-left: 12rem;
`

const Image = styled.img`
    max-width: 80%
`

const InfoContainer = styled.div`
    flex: 1;
`

const Title = styled.h1`
    font-size: 4rem;

`

const Desc = styled.p`
    margin: 30px 0px;
    font-size: 1.2 rem;
    font-weight: 500;
    letter-spacing: 2px;
`

const Button = styled.button`
    padding: 0.6rem;
    font-size: 1.4rem;
    background-color: transparent;
    cursor: pointer;
`


const Slider = () => {
  return (
    <Container>
      <Arrow direction ="left">
        <ArrowLeft/>
      </Arrow>
      <Wrapper>
        <Slide bg="fcf1ed">
          <ImgContainer>
            <Image src="/images/Staplovi.png"/>
          </ImgContainer>
          <InfoContainer>
            <Title>WINTER SALE 50% OFF</Title>
            <Desc>Power up your deck with low rarity singles at unmatched prices</Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
        <Slide bg="f5fafd">
          <ImgContainer>
            <Image src="/images/Frontpage.png"/>
          </ImgContainer>
          <InfoContainer>
            <Title>HIGH RARITY STAPLES</Title>
            <Desc>Bling out your deck with the shiniest cards</Desc>
            <Button>SHOP NOW</Button>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
        <ArrowRight/>
      </Arrow>
    </Container>
  )
}

export default Slider
