import React from 'react'
import styled from 'styled-components'


const Container = styled.div`
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
`

const Link = styled.a`
    position: relative;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    font-weight: 400;
    transition: 0.5s;
    font-size: 1.6rem;
    padding: 10px 20px;
    border: 2px solid teal;

    &:hover{
        background: teal;
        letter-spacing: 0.25em;
        box-shadow: 0 0 35px teal; 
    }

    i {
        position: absolute;
        inset: 0;
        display: block;
        z-index: 1;
    }

    i::before {
        content: '';
        position: absolute;
        top: 0;
        left: 80%;
        width: 10px;
        height: 7px;
        background: white;
        transform: translateX(-50%) skewX(325deg);
        transition: 0.5s;
    }

    &:hover i::before{
        
    }


`

const ButtonSpan = styled.span`
    color: white;
    text-shadow: -1px -1px 0 #000,  
                 2px -1px 0 #000,
                -1px  2px 0 #000,
                 2px  2px 0 #000;  
`

const Italic = styled.i`

`

const Button = () => {
  return (
    <Container>
        <Link>
            <ButtonSpan>SHOP NOW</ButtonSpan>
            <Italic/>
        </Link>
    </Container>
  )
}

export default Button
